import React, { useState } from 'react';
import axios from 'axios';

const BASE_URL = 'https://album-cover-search.herokuapp.com/api/album-cover'; // Update this if the backend is running on a different URL

const App: React.FC = () => {
  const [artist, setArtist] = useState('');
  const [album, setAlbum] = useState('');
  const [covers, setCovers] = useState<string[]>([]);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    try {
      const response = await axios.get(`${BASE_URL}?artist=${encodeURIComponent(artist)}&album=${encodeURIComponent(album)}`);
      setCovers(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container">
      <section className="section has-background-success-dark">
        <h1 className="title is-3">Album Cover Search</h1>
      </section>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-field">
            <label className="label">Artist</label>
            <div className="control">
              <input
                className="input"
                type="text"
                value={artist}
                onChange={(event) => setArtist(event.target.value)}
              />
            </div>
          </div>
          <div className="form-field">
            <label className="label">Album</label>
            <div className="control">
              <input
                className="input"
                type="text"
                value={album}
                onChange={(event) => setAlbum(event.target.value)}
              />
            </div>
          </div>
          <div className="form-field">
            <div className="control">
              <button className="button is-primary" type="submit">
                Search
              </button>
            </div>
          </div>
        </form>
      </div>
      <br />
      <div className="columns is-multiline">
        {covers.map((cover, index) => (
          <div className="column is-4" key={index}>
            <img src={cover} alt="Album Cover" />
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
